    if(e.target.value === "Asc"){
        e.preventDefault ();
        dispatch (orderByNameAz(e.target.value));
        setPage (1);
        setOrden (`Ordenado ${e.target.value}`)
    }else if(e.target.value === "Desc"){
        e.preventDefault ();
        dispatch (orderByNameZa(e.target.value));
        setPage (1);
        setOrden (`Ordenado ${e.target.value}`)
    }else{

    }
}


