import React from 'react'
import '../Card/card.css';
import { Link } from 'react-router-dom';
import { useState } from "react";
import Paginated from '../../components/paginado/paginado.jsx';

export default function Card ({id,image, name, temperament,weight}) {

 const [currentPage, setCurrentPage] = useState(1);
    const[cardsPerPage, setCardsPerPag ] = useState (8);
    const indexOfLastCards = currentPage * cardsPerPage;
    const indexOfFirstCards = indexOfLastCards - cardsPerPage;
    const currentCards = estadoCard.slice(indexOfFirstCards,indexOfLastCards)

    const paginateds = (pagNumber) => {
        setCurrentPage(pagNumber)
    }



  return (
      <div className='cardperro'>
        <Link to={`/dogs/${id}`}>
            <p>{name}</p>

        </Link>
         <div>

            <div className='containergeneral'>

                <div className='container1'>
                   <img className='breedimage' src= {image} alt = "breed.img" width='250px' />
                      <div className='namedog'>{name}</div>
                </div>



                <div className='container2'>
                      <div className='nameTemp'>TEMPERAMENTS:</div>
                         <div className='textotemp'>{temperament}</div>




                </div>
                   <div className='container3'>
                      <div className='numpeso'>PESO:</div>
                         <div>{weight}kg</div>
                   </div>

             </div>
          <Paginated  cardsPerPage={cardsPerPage} estadoCard= {estadoCard.length} paginateds = {paginateds} />
          </div>

       </div>

  )
}
